package com.logicea.cards.models;import com.fasterxml.jackson.annotation.JsonFormat;import com.fasterxml.jackson.annotation.JsonIgnore;import com.fasterxml.jackson.databind.annotation.JsonDeserialize;import com.fasterxml.jackson.databind.annotation.JsonSerialize;import com.fasterxml.jackson.datatype.jsr310.deser.LocalDateTimeDeserializer;import com.fasterxml.jackson.datatype.jsr310.ser.LocalDateTimeSerializer;import jakarta.persistence.Column;import jakarta.persistence.MappedSuperclass;import lombok.Getter;import lombok.Setter;import java.io.Serializable;import java.time.LocalDateTime;@Setter@Getter@MappedSuperclasspublic abstract class BaseEntity implements Serializable {    @Column(name = "DELETED", columnDefinition = "boolean default false")    private boolean deleted;    @JsonIgnore    @JsonFormat(pattern = "dd-MMM-yyyy HH:mm:ss")    @Column(name = "created_at", updatable = false, columnDefinition = "timestamp DEFAULT CURRENT_TIMESTAMP", nullable = false)    @JsonDeserialize(using = LocalDateTimeDeserializer.class)    @JsonSerialize(using = LocalDateTimeSerializer.class)    private LocalDateTime createdAt;    @JsonIgnore    @JsonFormat(pattern = "dd-MMM-yyyy HH:mm:ss")    @Column(name = "updated_at", insertable = false)    private LocalDateTime updateAt;    @JsonIgnore    @JsonFormat(pattern = "dd-MMM-yyyy HH:mm:ss")    @Column(name = "deleted_at", insertable = false)    private LocalDateTime deleteAt;}